class ResolveIdentifierNetworkOperationInstrumentedTest {
    private var identifierRepository: IdentifierRepository
    private val expectedIdentifierDocumentString =
        """{"@context":"https://www.w3.org/ns/did-resolution/v1","didDocument":{"id":"did:ion:EiBGMAR43ZWai9JKvh31ShjvNaX0dYRYI0XtLnZIa88CqQ?-ion-initial-.","@context":["https://www.w3.org/ns/did/v1",{"@base":"did:ion:EiBGMAR43ZWai9JKvh31ShjvNaX0dYRYI0XtLnZIa88CqQ?-ion-initial-."}],"publicKey":[{"id":"#Va8_sign_ION_1","controller":"","type":"EcdsaSecp256k1VerificationKey2019","publicKeyJwk":{"kty":"EC","crv":"secp256k1","x":"hA3EaZjvgGpi1AnWmSEzPhheI55MzhdwDUtXcAS36Fk","y":"E44pJp7_md6Z6-yDngOK9LYaW_LTC8BXgSVMQCZeNrA"}}],"authentication":["#Va8_sign_ION_1"]},"methodMetadata":{"operationPublicKeys":[{"id":"#Va8_sign_ION_1","controller":"","type":"EcdsaSecp256k1VerificationKey2019","publicKeyJwk":{"kty":"EC","crv":"secp256k1","x":"hA3EaZjvgGpi1AnWmSEzPhheI55MzhdwDUtXcAS36Fk","y":"E44pJp7_md6Z6-yDngOK9LYaW_LTC8BXgSVMQCZeNrA"}}],"recoveryKey":{"kty":"EC","crv":"secp256k1","x":"MDa8v7M4-DN-Vy6ZYYlPYnPtwtTMjvE9H3-wXX2VJKc","y":"BCpmR6pekJvBqEbpH4LpnuLwSJLGdrNPr3AhWIGmbX4"}},"resolverMetadata":{"driverId":"did:ion","driver":"HttpDriver","retrieved":"2020-06-03T05:35:28.619Z","duration":"102.5010ms"}}"""
    private val expectedIdentifierResponse = Serializer().parse(IdentifierResponse.serializer(), expectedIdentifierDocumentString)
    private val expectedIdentifier =
        "did:ion:EiBGMAR43ZWai9JKvh31ShjvNaX0dYRYI0XtLnZIa88CqQ?-ion-initial-."

    init {
        val context = InstrumentationRegistry.getInstrumentation().targetContext
        VerifiableCredentialSdk.init(context)
        identifierRepository = VerifiableCredentialSdk.identifierManager.identifierRepository
    }

    @Ignore("Calls the real service which currently is still flaky. Ignoring for now")
    @Test
    fun successfulResolutionTest() {
        runBlocking {
            val actualIdentifierResponse = ResolveIdentifierNetworkOperation(
                identifierRepository.apiProvider,
                "https://beta.discover.did.microsoft.com/1.0/identifiers",
                expectedIdentifier
            ).fire()
            assertThat(actualIdentifierResponse).isInstanceOf(Result.Success::class.java)
            assertThat((actualIdentifierResponse as Result.Success).payload.didDocument.id).isEqualTo(expectedIdentifierResponse.didDocument.id)
            assertThat(actualIdentifierResponse.payload.didDocument.publicKey).isEqualTo(expectedIdentifierResponse.didDocument.publicKey)
        }
    }

